#!/bin/bash

# Enterprise AI Infrastructure - TUI Service Monitor
# 
# Interactive text-based dashboard for monitoring all services and containers
# Similar to htop but for AI infrastructure services
#
# Usage: ./service_tui.sh

set -euo pipefail

# Configuration
SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
REFRESH_INTERVAL=3
LOG_FILE="${SCRIPT_DIR}/tui_monitor_$(date +%Y%m%d_%H%M%S).log"

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
MAGENTA='\033[0;35m'
CYAN='\033[0;36m'
WHITE='\033[1;37m'
GRAY='\033[0;37m'
NC='\033[0m' # No Color
BOLD='\033[1m'

# Service states
STATE_UNKNOWN="❓"
STATE_UP="✅"
STATE_DOWN="❌"
STATE_WARNING="⚠️ "

# Service status tracking
declare -A SERVICE_STATUS
declare -A SERVICE_RESPONSE_TIME
declare -A SERVICE_LAST_CHECK

# Initialize service mappings
init_services() {
    # Core Infrastructure Services
    SERVICES["10.0.5.100"]="Ollama|11434|/api/tags|LLM Inference Engine|Container: ollama"
    SERVICES["10.0.5.101"]="Qdrant|6333|/healthz|Vector Database|Container: qdrant"
    SERVICES["10.0.5.102"]="PostgreSQL|5432||Relational Database|Container: postgres"
    SERVICES["10.0.5.103"]="Redis|6379||Cache & Session Store|Container: redis"
    SERVICES["10.0.5.104"]="MinIO|9000|/minio/health/live|Object Storage|Container: minio"
    SERVICES["10.0.5.105"]="SearXNG|8080|/|Privacy Search Engine|Container: searxng"
    SERVICES["10.0.5.106"]="Langfuse|3002|/api/public/health|LLM Observability|Container: langfuse"
    SERVICES["10.0.5.107"]="Neo4j|7474|/|Graph Database|Container: neo4j"
    
    # AI/ML Services
    SERVICES["10.0.5.111"]="Tika|9998|/tika|Document Text Extraction|Container: tika"
    SERVICES["10.0.5.112"]="Docling|5001|/health|Document Parser|Container: docling"
    SERVICES["10.0.5.113"]="Whisper|9000|/health|Speech-to-Text|Container: whisper"
    SERVICES["10.0.5.114"]="LibreTranslate|5000|/languages|Translation API|Container: libretranslate"
    SERVICES["10.0.5.115"]="MCPO|8765|/health|MCP to OpenAPI Proxy|Container: mcpo"
    
    # DevOps & Development
    SERVICES["10.0.5.120"]="Gitea|3003|/|Git Server|Container: gitea"
    SERVICES["10.0.5.121"]="Prometheus|9090|/-/healthy|Metrics Collection|Container: prometheus"
    SERVICES["10.0.5.122"]="Grafana|3004|/api/health|Visualization & Dashboards|Container: grafana"
    SERVICES["10.0.5.123"]="Loki|3100|/ready|Log Aggregation|Container: loki"
    SERVICES["10.0.5.124"]="BookStack|3005|/|Wiki & Documentation|Container: bookstack"
    SERVICES["10.0.5.125"]="Metabase|3006|/api/health|Analytics & BI|Container: metabase"
    SERVICES["10.0.5.126"]="Playwright|3007|/health|Browser Automation|Container: playwright"
    SERVICES["10.0.5.128"]="Portainer|9443|/api/status|Docker Management|Container: portainer"
    SERVICES["10.0.5.129"]="Formbricks|3008|/|Survey Platform|Container: formbricks"
    
    # Communication & Business
    SERVICES["10.0.5.140"]="Mailcow|443||Mail Server|Container: mailcow"
    SERVICES["10.0.5.141"]="EspoCRM|3009|/|Customer Relationship Management|Container: espocrm"
    SERVICES["10.0.5.142"]="Matrix|8008|/_matrix/client/versions|Chat Server|Container: matrix"
    SERVICES["10.0.5.143"]="Element|3010|/|Matrix Web Client|Container: element"
    SERVICES["10.0.5.144"]="Superset|3011|/health|Business Intelligence|Container: superset"
    SERVICES["10.0.5.145"]="DuckDB|8089|/health|Analytical Database|Container: duckdb"
    SERVICES["10.0.5.146"]="Authentik|9000|/-/health/live|SSO & Identity Provider|Container: authentik"
    
    # Image Generation & A/V
    SERVICES["10.0.5.160"]="ComfyUI|8188|/|Image Generation Workflows|Container: comfyui"
    SERVICES["10.0.5.161"]="AUTOMATIC1111|7860|/|Stable Diffusion WebUI|Container: automatic1111"
    SERVICES["10.0.5.162"]="faster-whisper|8000|/v1/models|Optimized Speech-to-Text|Container: faster-whisper"
    SERVICES["10.0.5.163"]="openedai-speech|8001|/v1/models|Text-to-Speech|Container: openedai-speech"
    
    # Legacy services
    SERVICES["10.0.5.26"]="Nextcloud|443||Cloud Storage|Container: nextcloud"
    SERVICES["10.0.5.27"]="Supabase|8000|/health|Backend Platform|Container: supabase"
    
    # Traefik reverse proxy
    SERVICES["10.0.4.10"]="Traefik|8080|/ping|Reverse Proxy|Container: traefik"
    
    # User containers (10.0.5.200-249)
    for i in {200..249}; do
        USER_CONTAINERS["10.0.5.${i}"]="User Container ${i}|Container: user${i}"
    done
    
    # Initialize all services as unknown
    for ip in "${!SERVICES[@]}"; do
        SERVICE_STATUS[$ip]="UNKNOWN"
        SERVICE_RESPONSE_TIME[$ip]="N/A"
        SERVICE_LAST_CHECK[$ip]="Never"
    done
    
    for ip in "${!USER_CONTAINERS[@]}"; do
        SERVICE_STATUS[$ip]="UNKNOWN"
        SERVICE_RESPONSE_TIME[$ip]="N/A"
        SERVICE_LAST_CHECK[$ip]="Never"
    done
}

# Clear screen and move cursor to top-left
clear_screen() {
    clear
    tput cup 0 0
}

# Get terminal dimensions
get_terminal_size() {
    COLUMNS=$(tput cols)
    LINES=$(tput lines)
}

# Draw header
draw_header() {
    local time=$(date '+%Y-%m-%d %H:%M:%S')
    local uptime=$(uptime | awk -F'up ' '{print $2}' | awk -F',' '{print $1}')
    
    echo -e "${BOLD}${BLUE}╔════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════════